contracts/Escrow.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.18;

contract Escrow {
    address public payer;
    address public payee;
    address public arbiter;
    uint256 public amount;
    uint256 public fundedAt;
    bool public released;
    bool public refunded;

    event Funded(address indexed payer, uint256 amount);
    event ProofSubmitted(address indexed by, string proof);
    event Approved(address indexed by);
    event Released(address indexed to, uint256 amount);
    event Refunded(address indexed to, uint256 amount);
    event TimeoutClaimed(address indexed by);

    constructor(address _payer, address _payee, address _arbiter, uint256 _amount) payable {
        payer = _payer;
        payee = _payee;
        arbiter = _arbiter;
        amount = _amount;
    }

    modifier onlyPayer() {
        require(msg.sender == payer, "only payer");
        _;
    }

    modifier onlyArbiter() {
        require(msg.sender == arbiter, "only arbiter");
        _;
    }

    function fund() external payable onlyPayer {
        require(msg.value == amount, "incorrect amount");
        require(fundedAt == 0, "already funded");
        fundedAt = block.timestamp;
        emit Funded(msg.sender, msg.value);
    }

    function submitProof(string calldata proof) external {
        require(msg.sender == payee, "only payee");
        emit ProofSubmitted(msg.sender, proof);
    }

    function approve() external onlyArbiter {
        require(!released, "already released");
        released = true;
        payable(payee).transfer(amount);
        emit Released(payee, amount);
    }

    function refund() external onlyArbiter {
        require(!refunded, "already refunded");
        refunded = true;
        payable(payer).transfer(amount);
        emit Refunded(payer, amount);
    }

    function claimTimeout() external onlyPayer {
        require(block.timestamp > fundedAt + 30 days, "timeout not reached");
        require(!released, "already released");
        require(!refunded, "already refunded");
        refunded = true;
        payable(payer).transfer(amount);
        emit TimeoutClaimed(msg.sender);
    }
}

contracts/EscrowERC20.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.18;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract EscrowERC20 {
    address public payer;
    address public payee;
    address public arbiter;
    IERC20 public token;
    uint256 public amount;
    uint256 public fundedAt;
    bool public released;
    bool public refunded;

    event Funded(address indexed payer, uint256 amount);
    event ProofSubmitted(address indexed by, string proof);
    event Released(address indexed to, uint256 amount);
    event Refunded(address indexed to, uint256 amount);

    constructor(address _payer, address _payee, address _arbiter, address _token, uint256 _amount) {
        payer = _payer;
        payee = _payee;
        arbiter = _arbiter;
        token = IERC20(_token);
        amount = _amount;
    }

    modifier onlyPayer() { require(msg.sender == payer, "only payer"); _; }
    modifier onlyArbiter() { require(msg.sender == arbiter, "only arbiter"); _; }

    function fund() external onlyPayer {
        require(fundedAt == 0, "already funded");
        require(token.transferFrom(msg.sender, address(this), amount), "transfer failed");
        fundedAt = block.timestamp;
        emit Funded(msg.sender, amount);
    }

    function submitProof(string calldata proof) external {
        require(msg.sender == payee, "only payee");
        emit ProofSubmitted(msg.sender, proof);
    }

    function approve() external onlyArbiter {
        require(!released, "already released");
        released = true;
        require(token.transfer(payee, amount), "transfer failed");
        emit Released(payee, amount);
    }

    function refund() external onlyArbiter {
        require(!refunded, "already refunded");
        refunded = true;
        require(token.transfer(payer, amount), "transfer failed");
        emit Refunded(payer, amount);
    }
}

contracts/EscrowERC20Factory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.18;

import "./EscrowERC20.sol";

contract EscrowERC20Factory {
    event EscrowCreated(address indexed escrowAddress, address indexed payer, address indexed payee, address arbiter, address token, uint256 amount);

    address[] public escrows;

    function createEscrowERC20(address payer, address payee, address arbiter, address token, uint256 amount) external returns (address) {
        EscrowERC20 escrow = new EscrowERC20(payer, payee, arbiter, token, amount);
        escrows.push(address(escrow));
        emit EscrowCreated(address(escrow), payer, payee, arbiter, token, amount);
        return address(escrow);
    }

    function allEscrows() external view returns (address[] memory) {
        return escrows;
    }
}

